import React from "react";
import Contact from "../Contact";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Container from "react-bootstrap/Container";
import "./footer.css";
import "./cv.pdf";
import {
  MDBFooter,
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBIcon,
  MDBBtn,
} from "mdb-react-ui-kit";

import axios from 'axios'
import fileDownload from 'js-file-download'





export default function FooterAndContact() {


  const onButtonClick = () => {
    // using Java Script method to get PDF file
    fetch('SamplePDF.pdf').then(response => {
        response.blob().then(blob => {
            // Creating new object of PDF file
            const fileURL = window.URL.createObjectURL(blob);
            // Setting various property values
            let alink = document.createElement('a');
            alink.href = fileURL;
            alink.download = 'SamplePDF.pdf';
            alink.click();
        })
    })
}
  return (
    <MDBFooter
      className="text-center text-white"
      style={{ backgroundColor: "rgb(56, 44, 44)", display: "flex" }}
    >
      <MDBContainer
        className="pt-4"
        style={{
          display: "flex",
          alignContent: "center",
          flexDirection: "column-reverse",
          flexWrap: "wrap",
        }}
      >
        <section style={{}}>
          <h1 style={{ color: "white" }} className="pt-2">
            Contact Me
          </h1>

          <Container className="center">
            <Row>
              <Col xl="12">
                <Contact></Contact>
              </Col>
            </Row>
          </Container>

          {/* SOCIAL LINKS */}
          <MDBBtn
            rippleColor="dark"
            color="link"
            floating
            size="lg"
            className="text-dark m-1"
            id="iconStyle"
            role="button"
            onClick={() => {handleDownload('./cv', 'umairCV.pdf')}}
          >
            <MDBIcon fas className="fa-file-alt iconStyle" />
          </MDBBtn>
          <MDBBtn
            rippleColor="dark"
            color="link"
            floating
            size="lg"
            className="text-dark m-1"
            href="#!"
            role="button"
          >
            <MDBIcon fas className="fa-envelope iconStyle" />
          </MDBBtn>
          <MDBBtn
            rippleColor="dark"
            color="link"
            floating
            size="lg"
            className="text-dark m-1"
            href="#!"
            role="button"
          >
            <MDBIcon fab className="fa-linkedin iconStyle" />
          </MDBBtn>
          <MDBBtn
            rippleColor="dark"
            color="link"
            floating
            size="lg"
            className="text-dark m-1"
            href="#!"
            role="button"
          >
            <MDBIcon fab className="fa-github iconStyle" />
          </MDBBtn>
        </section>
      </MDBContainer>
    </MDBFooter>
  );
}
